import React, { useState, useEffect } from "react";

function Weather(props){
    const [input, setInput] = useState('')
    const [cityData, setCityData] = useState({});
    const [cityZip, setCityTitle] = useState("");
    const apikey = `393db2c9cceb90d947389946e1acd3c2`;
        const cityUrl = `https://api.openweathermap.org/data/2.5/weather?zip=${cityZip}&units=imperial&appid=${apikey}`;
        useEffect(() => {
        const makeApiCall = async () => {
            const res = await fetch(cityUrl);
            const json = await res.json();
            console.log(json);
            setCityData(json);
        };
        makeApiCall();
    }, [cityZip]);

    const handleSubmit = e => {
        e.preventDefault();
        // props.handleSubmit(cityZip);
        setCityTitle(input);
    };

    const handleChange = e => {
        setInput(e.target.value)
      };
      console.log("WeatherInfo - props", props);
return (
    <div>
        <section>
        <form onSubmit={handleSubmit}>
        <label htmlFor="cityTitle">Zip Code</label>
        <input
          id="cityTitle"
          type="text"
          value={input}
          onChange={handleChange}
        />
        <input type="submit" value="Search" />
    </form>

    {/* <div>
      <h1>here is info</h1>
      <h2>city: {cityData.name} </h2>
      <h3>Min Temp:{cityData.main.temp_min} &deg;F</h3>
      <h3>Max Temp:{cityData.main.temp_max} &deg;F</h3>
      <h3>Description:{cityData.weather[0].description} </h3>
    </div> */}
    </section>
    </div>
    );
 }     

 export default Weather;
