import React, { useState, useEffect } from "react";

function Weather(props){
  const [cityData, setCityData] = useState({});
  const [cityZip, setCityTitle] = useState("");

  //useEffect(() => {}, []);
  //${word}


  useEffect(() => {
    const apikey = `393db2c9cceb90d947389946e1acd3c2`;
    const cityUrl = `https://api.openweathermap.org/data/2.5/weather?zip=${cityZip}&units=imperial&appid=${apikey}`;
    const makeApiCall = async () => {
      const res = await fetch(cityUrl);
      const json = await res.json();
      console.log(json);
      setCityData(json);
    };
    makeApiCall();
  }, [cityZip]);

  const handleSubmit = e => {
    e.preventDefault();
    props.handleSubmit(cityZip);
    // reset the form
    setCityTitle("");
  };
{/* <Form handleSubmit={handleSubmit} />
      <WeatherInfo city={cityData} /> */}
      const handleChange = e => {
        const temp = e.target.value;
        setCityTitle(temp);
      };
    return (
        <div>
        <form onSubmit={handleSubmit}>
        <label htmlFor="cityTitle">Zip Code</label>
        <input
          id="cityTitle"
          type="text"
          value={cityZip}
          onChange={handleChange}
        />
        <input type="submit" value="Search" />
      </form>
      <div>
      <h1>here is info</h1>
      <h2>city: {props.city.name} </h2>
      <h3>Min Temp:{props.city.main.temp_min} &deg;F</h3>
      <h3>Max Temp:{props.city.main.temp_max} &deg;F</h3>
      <h3>Description:{props.city.weather[0].description} </h3>
    </div>
    </div>
    );
 }     

 export default Weather;
